$(window).scroll(function() {
    var scrollTop = $(window).scrollTop();
    var initialHeight = 300; // This should match your CSS for #gtco-counter
    var newHeight = Math.max(50, initialHeight - scrollTop * 0.5); // Control the rate of shrinkage
    var scale = Math.max(0.1, newHeight / initialHeight); // Avoid scaling to zero
    var offsetTop = (newHeight / 2) + scrollTop * 0.9; // Modified to move down more as section shrinks

    $('#gtco-counter').css({
        'height': newHeight + 'px',
        'visibility': (newHeight > 50 ? 'visible' : 'hidden') // Hide section at 50px height
    });

    $('.centered-logo').css({
        'transform': 'translate(-50%, -50%) scale(' + scale + ')',
        'opacity': (newHeight > 50 ? 1 : 0), // Hide logo at 50px height
        'top': offsetTop + 'px' // Adjust top to keep it in view longer
    });
});

// Optionally reset when scrolling back to the top
$(window).scroll(function() {
    if ($(window).scrollTop() === 0) {
        $('#gtco-counter').css({
            'visibility': 'visible',
            'height': '300px' // Reset to the initial height
        });
        $('.centered-logo').css({
            'transform': 'translate(-50%, -50%) scale(1)',
            'opacity': 1,
            'top': '200px' // Re-center at initial position
        });
    }
});
